DROP TABLE Permissions cascade constraints;
DROP TABLE SiteUsers cascade constraints;
DROP TABLE Friends cascade constraints;
DROP TABLE Messages cascade constraints;
DROP TABLE SiteGroups cascade constraints;
DROP TABLE ManageGroups cascade constraints;
DROP TABLE Photos cascade constraints;
DROP TABLE Posts cascade constraints;
DROP TABLE SiteComments cascade constraints;
DROP TABLE Includes cascade constraints;

CREATE TABLE Permissions(
    permissionID INT GENERATED BY DEFAULT ON NULL AS IDENTITY,
    permission_name VARCHAR(100),
    PRIMARY KEY(permissionID)
);

CREATE TABLE SiteUsers(
    permissionID INT,
    sur_name VARCHAR2(100),
    last_name VARCHAR2(100),
    email VARCHAR2(100),
    passwords VARCHAR2(100),
    workplace VARCHAR2(100),
    school VARCHAR2(100),
    birth_year INT,
    birth_month INT,
    birth_day INT,
    PRIMARY KEY(email),
    FOREIGN KEY(permissionID) REFERENCES Permissions(permissionID) ON DELETE CASCADE
);

CREATE TABLE Friends(
    friendEmail1 VARCHAR(100),
    friendEmail2 VARCHAR(100),
    friend_start_date VARCHAR(100),
    is_friend INT,
    FOREIGN KEY(friendEmail1) REFERENCES SiteUsers(email) ON DELETE CASCADE,
    FOREIGN KEY(friendEmail2) REFERENCES SiteUsers(email) ON DELETE CASCADE
);

CREATE TABLE Messages(
    messageID INT GENERATED BY DEFAULT ON NULL AS IDENTITY,
    senderEmail VARCHAR(100),
    receiverEmail VARCHAR(100),
    message_text VARCHAR(1000),
    send_date VARCHAR(100),
    PRIMARY KEY(messageID),
    FOREIGN KEY(senderEmail) REFERENCES SiteUsers(email) ON DELETE CASCADE,
    FOREIGN KEY(receiverEmail) REFERENCES SiteUsers(email) ON DELETE CASCADE
);

CREATE TABLE SiteGroups(
    groupID INT GENERATED BY DEFAULT ON NULL AS IDENTITY,
    ownerEmail VARCHAR(100),
    name VARCHAR(100),
    PRIMARY KEY(groupID),
    FOREIGN KEY(ownerEmail) REFERENCES SiteUsers(email) ON DELETE CASCADE
);

CREATE TABLE ManageGroups(
    email VARCHAR(100),
    groupID INT,
    join_date VARCHAR(100),
    FOREIGN KEY(email) REFERENCES SiteUsers(email) ON DELETE CASCADE,
    FOREIGN KEY(groupID) REFERENCES SiteGroups(groupID) ON DELETE CASCADE
);

CREATE TABLE Photos(
    photoID INT GENERATED BY DEFAULT ON NULL AS IDENTITY,
    email VARCHAR(100),
    photo_type VARCHAR(100),
    photo_url VARCHAR(1000),
    photo_size INT,
    PRIMARY KEY(photoID),
    FOREIGN KEY(email) REFERENCES SiteUsers(email) ON DELETE CASCADE
);

CREATE TABLE Posts(
    postID INT GENERATED BY DEFAULT ON NULL AS IDENTITY,
    email VARCHAR(100),
    photoID INT,
    groupID INT,
    post_text VARCHAR(1000),
    post_date VARCHAR(100),
    PRIMARY KEY(postID),
    FOREIGN KEY(email) REFERENCES SiteUsers(email) ON DELETE CASCADE,
    FOREIGN KEY(photoID) REFERENCES Photos(photoID) ON DELETE CASCADE,
    FOREIGN KEY(groupID) REFERENCES SiteGroups(groupID) ON DELETE CASCADE
);

CREATE TABLE SiteComments(
    commentID INT GENERATED BY DEFAULT ON NULL AS IDENTITY,
    postID INT,
    email VARCHAR(100),
    comment_text VARCHAR(1000),
    comment_date VARCHAR(100),
    PRIMARY KEY(commentID),
    FOREIGN KEY(postID) REFERENCES Posts(postID) ON DELETE CASCADE,
    FOREIGN KEY(email) REFERENCES SiteUsers(email) ON DELETE CASCADE
);

CREATE TABLE Includes(
    postID INT,
    commentID INT,
    FOREIGN KEY(postID) REFERENCES Posts(postID) ON DELETE CASCADE,
    FOREIGN KEY(commentID) REFERENCES SiteComments(commentID) ON DELETE CASCADE
);











